package vo;

public class StoreClerk {
	private String clerkNum;
	private String FrRegiNum;
	private String clerkName;
	private String gender;
	private String residentNum;
	private String phoneNum;
	private String address;
	private String hourlyPay;
	private int pay;
	private double workhour;
	private String ontime;
	private String offtime;
	private String monthDate;
	private String years;
	public StoreClerk() {
		super();
		// TODO Auto-generated constructor stub
	}
	public int getPay() {
		return pay;
	}
	public void setPay(int pay) {
		this.pay = pay;
	}
	public double getWorkhour() {
		return workhour;
	}
	public void setWorkhour(double workhour) {
		this.workhour = workhour;
	}
	public String getClerkNum() {
		return clerkNum;
	}
	public void setClerkNum(String clerkNum) {
		this.clerkNum = clerkNum;
	}
	public String getFrRegiNum() {
		return FrRegiNum;
	}
	public void setFrRegiNum(String frRegiNum) {
		FrRegiNum = frRegiNum;
	}
	public String getClerkName() {
		return clerkName;
	}
	public void setClerkName(String clerkName) {
		this.clerkName = clerkName;
	}
	public String getGender() {
		return gender;
	}
	public void setGender(String gender) {
		this.gender = gender;
	}
	public String getResidentNum() {
		return residentNum;
	}
	public void setResidentNum(String residentNum) {
		this.residentNum = residentNum;
	}
	public String getPhoneNum() {
		return phoneNum;
	}
	public void setPhoneNum(String phoneNum) {
		this.phoneNum = phoneNum;
	}
	public String getAddress() {
		return address;
	}
	public void setAddress(String address) {
		this.address = address;
	}
	public String getHourlyPay() {
		return hourlyPay;
	}
	public void setHourlyPay(String hourlyPay) {
		this.hourlyPay = hourlyPay;
	}
	public String getOntime() {
		return ontime;
	}
	public void setOntime(String ontime) {
		this.ontime = ontime;
	}
	public String getMonthDate() {
		return monthDate;
	}
	public void setMonthDate(String monthDate) {
		this.monthDate = monthDate;
	}
	public String getOfftime() {
		return offtime;
	}
	public void setOfftime(String offtime) {
		this.offtime = offtime;
	}
	public String getYears() {
		return years;
	}
	public void setYears(String years) {
		this.years = years;
	}
}
